custom-widgets.scss:
.custom-slider-widget {
    margin: 40px 0;
    &.container {
        padding: 0 10px;
    }
    @media screen and (min-width: 475px) {
        width: 100%;
    }
    @media screen and (min-width: 1024px) {
        width: 100%; padding: 0 0px;
    }
    .slick-initialized .slick-slide{
        padding: 0 5px;
    }
    .slider-title {
        text-align: center; font-size: 24px; font-weight: 500; margin-bottom: 15px;
        a {
            text-decoration: none;
        }
    }
    .slide-wrapper-inner {
        text-align: center; text-decoration: none; display: block; transition: all 0.2s ease-in-out; text-transform: uppercase;
        &:hover {
            opacity: 0.8;
        }
        .slide-image-wrapper {
            display: block; margin-bottom: 3px;
            img {
                max-width: 127px; margin: auto;
            }
        }
        span {
            font-family: inherit;
        }
    }
    .slick-prev {
        left: 0px;
    }
    .slick-next {
        right: 0px;
    }
    .slick-prev, .slick-next {
        padding: 20px 4px; width: 22px; background-color: white;
    }
    .slick-prev::before, .slick-next::before {
        background-size: 10px !important;
    }
}

// Custom top bar - start
.top-bar {
    background-color: #fff;
}
.promotion_bar {
    height: 32px; transition: all 0.4s ease-in-out;
    div {
        font-size: 0; opacity: 0; transition: all 0.4s ease-in-out; height: auto;
        p {
            margin: 0;
        }
    }
    div.current {
        opacity: 1; height: 100%; display: flex; align-items: center; justify-content: center;
        p {
            width: 100%; font-size: 12px; font-weight: 500; padding: 4px; line-height: 13px;
            @media screen and (min-width: 475px) {
                font-size: 13px; line-height: 20px; padding: 7px;
            }
            * {
                text-decoration: none; font-size: inherit; line-height: inherit; color: inherit;
            }
        }
    }
}
// Custom top bar - end

// Custom image banner - start
.custom_image_banner {
    height: 200px; width: 100%; max-width: 1440px; padding: 0 15px; margin: 0 auto;
    @media screen and (min-width: 991px) {
        padding: 0 20px;
    }
    .desktop_banner, .mobile_banner {
        a {
            width: 100%;height: 100%; display: inline-block;
        }
        img {
            width: 100%; height: auto; display: none;
        }
    }
    .desktop_banner {
        display: none; height: auto; max-height: 425px; overflow: hidden;
        @media screen and (min-width: 768px) {
            display: block;
        }
        img {
            height: 100%; object-fit: cover;
        }
    }
    .mobile_banner {
        display: block; padding: 15px 0 0;
        @media screen and (min-width: 768px) {
            display: none;
        }
    }
}
// Custom image banner - end

// Scrolling banners section - start
.custom_scrolling_banners {
    .main_heading {
        color: #000000; font-size: 24px;
        @media screen and (min-width: 1024px) {
            font-size: 30px;
        }
        a {
            &:hover {
                color: #787878;
            }
        }
    }
    .wrapper_full_width {
        width: 100%; overflow-x: scroll;
        /* scroll for firefox */
    	scrollbar-width: none;
    	scrollbar-color: #000000 transparent;
    	/* scroll for webkit browsers */
    	&::-webkit-scrollbar {
    		height: 0px; background-color: transparent; cursor: pointer;
    	}
    	&::-webkit-scrollbar-thumb {
    		background-color: #eeeeee;
    		&:hover {
    			background-color: darken(#eeeeee, 15%);
    		}
    	}
        .scrolling_banners_wrapper {
            display: flex; justify-content: space-between;
            .banner_wrapper {
                width: 150px; display: inline-block; text-decoration: none; text-align: center;  margin-right: 15px;
                @media screen and (min-width: 768px) {
                    width: 260px; margin-right: 20px;
                    &:last-child {
                        margin-right: 0;
                    }
                }
                &:hover {
                    .custom_scrolling_banner {
                        opacity: 0.6;
                    }
                }
                .custom_scrolling_banner {
                    height: 150px; width: 150px; transition: 0.3s ease-in-out;
                    @media screen and (min-width: 768px) {
                        height: 250px; width: 260px;
                    }
                }
                .banner_subtitle {
                    font-size: 15px; font-weight: 500; margin: 15px 0 20px; text-transform: unset;
                }
            }
        }
    }
}
// Scrolling banners section - end

// Custom button links - start
.button-links-wrap {
    display: flex; justify-content: space-between; align-items: center;margin: 0px 0 20px;  flex-wrap: wrap;
    @media (min-width: 768px) {
        flex-wrap: nowrap;
        margin: 20px 0;
    }
    @media (min-width: 1024px) {
        margin: 20px 0 25px;
    }
    a.button-link {
        display: inline-block; width: 48%; text-align: center; padding: 10px; margin: 0 0 10px 0; border: 1px solid #000; font-size: 16px; line-height: 28px; letter-spacing: 1px; text-transform: unset; transition: all 0.3s ease-in-out;
        @media (min-width: 768px) {
            margin-bottom: 10px; width: 100%;
            &:not(:last-child){
                margin-right: 10px;
            }
        }
        &:first-child {
            background: #000000; color: #ffffff;
            @media (min-width: 768px) {
                display: none;
            }
        }
        &:hover {
            background: #000; color: #fff;
        }
    }
}
// Custom button links - end

// Custom Promotion Banner - start
.custom_promotion_banners {
    display: flex; width: 100%; justify-content: space-between; flex-wrap: wrap;
    .banner_wrapper {
        width: 48.55%; margin-bottom: 5px; position: relative;
        @media (min-width: 768px) {
            margin-bottom: 25px;
        }
        .custom_banner {
            position: absolute; height: 100%; display: flex; width: 100%; flex-direction: column; justify-content: center; align-items: center;
            @media screen and (min-width: 991px) {
                padding: 18px;
            }
            .top_heading {
                color: #000; font-size: 15px; letter-spacing: 3px; margin-bottom: 5px; display: none;
                @media screen and (min-width: 991px) {
                    display: block; margin-bottom: 10px;
                }
            }
            .main_heading {
                color: #000000; font-size: 24px; line-height: 28px; word-break: break-word; font-weight: 600; margin: 0 0 10px 0;
                @media screen and (min-width: 991px) {
                    font-size: 30px; line-height: 34px;
                }
                
            }
            .button_custom_promo_banner {
                margin: 0; border: none; padding: 0 0 7px 0; border-radius: 0; color: #000;  font-size: 19px; font-weight: 500; border-bottom: 2px solid #000;
            }
        }
        .promo-desktop-image {
            display: none; width: 100%;
        }
        .promo-mobile-image {
            display: block; width: 100%;
        }
        @media screen and (min-width: 768px) {
            .promo-desktop-image {
                display: block;
            }
            .promo-mobile-image {
                display: none;
            }
        }
    }
}
// Custom Promotion Banner - end

// Text and bg image css - start
.custom_text_with_bgimage {
    .text_with_image {
        text-align: left; z-index: 1; padding: 15px;
        h2, h5 {
            margin: 10px 0;
        }
        .top_heading {
            font-size: 16px; font-weight: 500; letter-spacing: 5px; text-transform: none;
        }
        .main_heading {
            font-size: 35px; font-weight: 600;
            @media screen and (min-width: 475px) {
                font-size: 40px;
            }
        }
        .text_content {
            font-weight: 500; font-size: 16px; margin-bottom: 20px;
        }
        .button {
            background: #000000; border: none; color: #ffffff; text-transform: uppercase; letter-spacing: 2px; padding: 15px 60px; border-radius: 0px; font-weight: 500;
            @media screen and (min-width: 475px) {
                padding: 15px 85px;
            }
        }
    }
}
// Text and bg image css - end

// Custom table - start
.custom_table_wrapper {
    display: grid; grid-template-columns: 1fr 1fr 1fr 1fr; text-align: left;
    @media screen and (max-width: 991px) {
        grid-template-columns: 1fr;
    }
    &.columns_4 {
        grid-template-columns: 1fr 1fr 1fr 1fr;
        @media screen and (max-width: 991px) {
            grid-template-columns: 1fr;
        }
    }
    &.columns_3 {
        grid-template-columns: 1fr 1fr 1fr;
        @media screen and (max-width: 991px) {
            grid-template-columns: 1fr;
        }
    }
    &.columns_2 {
        grid-template-columns: 1fr 1fr;
        @media screen and (max-width: 991px) {
            grid-template-columns: 1fr;
        }
    }
    &.columns_1 {
        grid-template-columns: 1fr;
    }
    .column:nth-child(odd) {
        background-color: #F8F8F9;
    }
    .column:nth-child(even) {
        background-color: #FFFFFF;
    }
    h1, h2, h3, h4, h5, h6 {
        font-weight: 700; margin: 0 0 10px;
    }
    h5 {
        font-size: 13px;
    }
    h5 {
        font-size: 14px;
    }
    h4 {
        font-size: 16px;
    }
    h3 {
        font-size: 18px;
    }
    h2 {
        font-size: 21px;
    }
    p, a  {
        font-size: 13px; letter-spacing: 0.4px;
    }
    .column_title {
        position: relative;
        * {
            font-weight: 500; margin: 0;
        }
        @media screen and (max-width: 991px) {
            &:after {
                position: absolute; content: "+"; font-size: 30px; height: 100%; width: auto; background: transparent; right: 10px; display: flex; top: 0; align-items: center;
            }
            &:before {
                position: absolute; content: "-"; font-size: 30px; height: 100%; width: auto; background: transparent; right: 10px; display: none; top: 0; align-items: center;
            }
            &.is-open {
                &:after {
                    display: none;
                }
                &:before {
                    display: flex;
                }
            }
        }
    }
    .column_text_wrapper {
        @media screen and (max-width: 991px) {
            display: none;
            &.is-open {
                display: block;
            }
        }
    }
    .row {
        @media screen and (max-width: 991px) {
            height: auto; margin: 0 !important; padding: 20px !important;
        }
        @media screen and (max-width: 475px) {
            height: auto; margin: 0 !important; padding: 20px 10px !important;
        }
    }
    .button-table {
        background: #fff; color: #000; border: 1px solid #000; border-radius: 0; padding: 15px 0; width: 100%; margin-bottom: 15px;
    }
}
// Custom table - end



