// Custom list widget - start
.custom_list {
    margin: 20px 0 30px;
    .page-heading {
        text-align: left; font-size: 18px; color: #000000; margin-bottom: 10px;
        @media (min-width: 568px) {
            font-size: 20px;
        }
        @media (min-width: 1024px) {
            font-size: 23px;
        }
    }
    .custom_unordered_list {
        list-style: none; margin: 0; height: 250px; overflow: hidden;
        @media (min-width: 768px) {
            column-count: 2; height: auto;
        }
        @media (min-width: 991px) {
            column-count: 3;
        }
        .custom_list_item {
            text-align: left; padding: 2px 0 0;
            &
            .list_item_title {
                margin: 0;
                a {
                    font-size: 20px; line-height: 24px; color: #000000;
                    @media (min-width: 568px) {
                        font-size: 24px;
                    }
                    @media (min-width: 1024px) {
                        font-size: 30px; line-height: 35px;
                    }
                    &:hover {
                        color: #787878;
                    }
                }
            }
        }
    }
    .expand_list {
        text-decoration: underline; text-align: left; margin-top: 10px; font-size: 12px;
        @media (min-width: 768px) {
            display: none;
        }
        .expand_less {
            display: none;
        }
        &.list_expanded {
            .expand_less {
                display: inline;
            }
            .expand_more {
                display: none;
            }
        }
    }
    &.custom_list_expanded .custom_unordered_list {
        height: auto;
    }
}
// Custom list widget - end

// Custom Category List widget
main#main-content {
    position: relative;
    z-index: 3;
    overflow: hidden;
}
.header{background: #fff;}

[data-content-region="category_page_below_banner"] [data-sub-layout] {
    padding: 0;
}
.catgegory-lists{
    padding: 10px 0;
    border-top: 1px solid #ececec;
    border-bottom: 1px solid #ececec;
    //margin-top:30px;
    background: #fff;
    width: 100%;
    &.sticky{
        margin: auto;
    }
    @media (max-width:800px){
        border-top:none;
        margin-top:0;
        border-bottom: none;
    }
         .cat_lists {
            overflow: hidden;
            position: relative;
            margin-top: 0;
            .cat_list {
                display: flex;
                scroll-snap-type: x mandatory;
                overflow-x: scroll;
                scroll-behavior: smooth;
                margin: 0;
                padding: 0;
                list-style: none;
                @media (max-width:800px){
                    padding: 0 0 9px;
                    &::-webkit-scrollbar-thumb {
                        background-color: #212121;
                      }
                }
                &::-webkit-scrollbar {
                    height:0px;
                    @media screen and (max-width: 800px) {
                        width: 4px;
                        height:4px
                    }
                }

                li{
                    margin-right: 6px;
                    flex-shrink: 0;
                    &:last-child {
                        margin-right: 0;
                    }
                   a {
                        border-radius: 4px;
                        display: flex;
                        padding: 8px 8px;
                        line-height: 1.4;
                        border: 1px solid #727272;
                        transition: all 0.5s;
                        font-size: 14px;
                        font-weight: 500;
                        &:hover,&:focus{
                            border-color: #000;
                            color: #000;
                        }
                    } 
                }
            }
            .prev, .next {
                position: absolute;
                top: -1px;
                cursor: pointer;
                background-color: #fff;
                border: none;
                padding: 0;
                z-index: 10;
                width: 44px;
                height: 104%;
                display: flex;
                align-items: center;
                justify-content: center;
                left:0;
                @media (max-width:800px){
                    display: none !important;
                }
                & svg {
                    width: 24px;
                    height: 24px;
                    transition: all 0.5s;
                    &:hover{
                        opacity: 0.5;
                    }
                }
            
            }
            .prev {
                transform: rotate(180deg);
                left:0;
            }
            
            .next {
              right: 0;
              left:auto;
            }
        }
}

