customstyle.scss:
// Common css START

.body{
    margin-top: 0; margin-bottom: 0;
}
#main-content{
    min-height: 500px; margin-top: 10px;
    @media (min-width: 801px) {
        margin-top: 0;
    }
}
.slick-dots li {
    height: 15px; width: 15px; margin: 0; padding: 0.35714rem;
    @media screen and (min-width:568px){
        height: 20px; width: 20px;
    }
    button{
        height: 10px; width: 10px;
        @media screen and (min-width:568px){
            height: 12px; width: 12px; 
        }
        &:before{
            height: 10px; width: 10px;
            @media screen and (min-width:568px){
                height: 12px; width: 12px;
            }
        }
    }
    &.slick-active button::before {
        background: #000000;
    }
} 
.snize-recommendation .snize-dots li  {
    height: 15px; width: 15px; margin: 0; padding: 0 !important; margin: 0 0 !important;
    @media screen and (min-width:568px){
        height: 20px; width: 20px;
    }
    button{
        height: 10px !important; width: 10px !important; background:#fff !important; border: 1px solid #000 !important; transition: all 0.4s ease-in-out;
        @media screen and (min-width:568px){
            height: 12px !important; width: 12px !important; 
        }
        &:before{
            height: 10px !important; width: 10px !important;
            @media screen and (min-width:568px){
                height: 12px !important; width: 12px !important;
            }
        }
    }
    &.slick-active button::before {
        background: #000000;
    }
    &.active button, &:hover button{
            background:#000 !important;
        }
} 
.snize-recommendation-results{
    .carousel-tooltip{
        display: none; 
    }
    .snize-title{
            font-family: Montserrat,Arial,Helvetica,sans-serif!important; -ms-flex: none; flex: none;  overflow: hidden; line-height: 1.1;
    height: 34px;margin: 6px 0;text-overflow: ellipsis;width: 100%; padding: 0; font-size: 14px; letter-spacing: .25px;-ms-flex-align: center;align-items: center;text-align: center;-ms-flex-item-align: center;
    -ms-grid-row-align: center;align-self: center;font-weight: 400!important;
    }
    .badge-never-worn, .badge-vintage{
        position: absolute;top: 54%;  transform: translateY(-50%);  right: 0px; 
        @media screen and (min-width:1024px){
             right: 20px; 
        }
    }
    .badge-with-tag{
        position: absolute;top: 56.8%;  transform: translateY(-50%);  right: 0px; 
        @media screen and (min-width:1024px){
             right: 20px; 
        }
        svg{
            fill: #000;
        }
        
    }
} 
.badges, .badge-never-worn, .badge-vintage, .badge-with-tag{
    margin-bottom: 3px;
    .icon {
        width: 64px;
        @media screen and (min-width:768px){
            width: 120px;
        }
    } 
} 
.badge-never-worn{
    width: 86px;
}
.btm-line{
    position: relative; padding-bottom: 7px;
    &:after{
        content: ''; position: absolute; bottom: 0; width: 40px; height: 3px; left: 50%; transform: translateX(-50%); background-color: #000;
    }
}
.modal{
    overflow-y: auto;
    .confirm{
        background-color: #000000 !important; transition: all 0.4s ease-in-out;
        &:hover{
            background-color: #ffffff !important; color: #000000 !important;
        }
    }
    .cancel{
        background-color: #ffffff !important; transition: all 0.4s ease-in-out;
        &:hover{
            background-color: #000000 !important; color: #ffffff !important;
        }
    }
}
// Common css END 
// Header CSS START 
.header{
    position: fixed; top: 0; padding-bottom: 5px; transition: all 0.4s ease-in; background-color: transparent; z-index: 101; box-shadow: 0px 0px 6px 2px rgba(0,0,0,0);
    
    @media (min-width: 801px) {
        padding-bottom: 0;
    }
    .promotion_bar { 
        &.on_scroll_show { 
            height: 32px; 
        } 
        &.on_scroll_hide { 
            height: 0; 
            p { 
                padding: 0; opacity: 0; line-height: 0; 
            } 
        } 
    }
    .navUser-item-currency{
        a{
            @media screen and (min-width:801px){
                font-size: 0;
            }
            i{
                @media screen and (min-width:801px){
                    display: none;
                }
            }
        }
    }
    &.is-open{
        .navUser-item.navUser-item-currency {
            display: block; width: 100%;
            >a{
                padding-left: 0;
            }
        }
    }
}
body{
    padding-top: 90px;
    @media screen and (min-width:801px){
        padding-top: 113px;
    }
    &.one-bar-padding{
        padding-top:121px;
        @media screen and (min-width:801px){
            padding-top: 143px;
        }
    }
    &.two-bar-padding{
        padding-top:154px;
        @media screen and (min-width:801px){
            padding-top: 177px;
        }
    }
    &.one-bar-padding, &.two-bar-padding {
        .custom_image_banner {
            height: auto;
        }
    }
}
.promo-text {
    margin-top: 35px; z-index: -1; transition:all 0.4s ease-in-out;
    @media screen and (min-width: 801px) {
        margin-top: 0px;
    }
}
.sticky-promo{
    position: fixed; background-color: #000;top: 50%; right: 17px;color: #fff;padding: 5px 15px;text-transform: uppercase;transform: translateY(-50%);
    transform: translate(50%, 50%) rotate(90deg);font-size: 16px; font-weight: 600; border: 1px solid  #6c6b6b; font-family: 'Montserrat', sans-serif;
    z-index: 2; transition: all 0.3s ease-in-out;
    &:hover{
        background-color: #fff; color: #000;
    }
    @media screen and (min-width: 568px){
        font-size: 20px; padding: 9px 25px; right: 21px;
    }
}
// Header CSS END 
.lc-inner{
    text-align: center; padding: 12px 0 20px 0; min-height: 143px;
    @media screen and (min-width:768px) {
        min-height: 165px;
    }
    @media screen and (min-width:801px) {
        padding: 20px 0 25px 0; min-height: 170px;
    }
    @media screen and (min-width:801px) {
        padding: 40px 0 25px;
    }
    #top-brands-2{
        padding-bottom: 0;
        @media screen and (min-width: 1024px){
            padding-bottom: 20px;
        }
    }
    .container{
        padding: 0 0;
    }
    figure{
        margin-top: 0;
        @media screen and (min-width:801px){
            margin-top: 2rem;
        }
    } 
    h1{
        font-size: 20px; line-height: 24px; margin-bottom: 10px; font-weight: 600; letter-spacing: 1px; margin-top: 0;
        @media screen and (min-width:568px){
            font-size: 24px; line-height: 32px;
        }
        a{
            text-decoration: none;
        }
    }
    p{
        font-size: 14px; line-height: 21px; font-weight: 400; letter-spacing: 1px;
    }
    .lc-subheading{
        font-size: 1rem; margin-bottom: 0; line-height: 19px; margin-bottom: 15px;
        @media screen and (min-width:991px){
            margin-bottom: 0;
        }
    }
    .lc-para-txt{
        max-height: 20px; overflow: hidden; position: relative;   white-space: nowrap; text-overflow: ellipsis; display: none;
        @media screen and (min-width:991px){
            display: block; margin-bottom: 0px;
        }
        &.expand{
            max-height: 100%; overflow: visible; white-space: inherit; text-align: justify;
            &:before{
                display: none;
            }
            &:after{
                content: 'less';background-color: #fff; text-decoration: underline; cursor: pointer;
            }
        }
        &:before{
            content: 'More';float: right; background-color: #fff; text-decoration: underline; cursor: pointer;
        }
    }
    .lc-list-wrap{
        display: none;
        &.slick-slider {
            display: block;
        }
        .lc-list{
            padding: 0 5px;
            @media screen and (min-width:568px) {
                padding: 0 10px;
            }
            h3, h4{
                margin: 0;
                font-size: 12px;
                line-height: 16px;
                text-transform: uppercase;
            }
            a{
                display: flex; flex-wrap: wrap; flex-direction: column; text-decoration: none;
                .lc-img-wrap{
                    margin-bottom: 5px;
                    img{
                        max-height: 135px; width: auto; margin: 0 auto;
                    }
                }
                .lc-title{
                    font-size: 12px; line-height: 16px; text-transform: uppercase;
                }
            }
        }
    }
    .slick-prev,.slick-next{
        width: 25px; padding: 0;
    }
}
.slick-next, .slick-prev {
    padding: 4px;
    &::before, &::before {
            height: 20px; width: 10px;
    }
}
.slick-prev{
    left: 0;
}
.slick-next{
    right: 0;
}

.two-part-sec{
     display: flex; flex-wrap: wrap; align-items: center; flex-direction: column; margin-bottom: 30px; 
    @media screen and (min-width:768px){
         flex-direction: row;
    }
    @media screen and (min-width:1199px){
        padding-left: 5%; padding-right: 5%;  flex-direction: row;
    }
    .left-part{
        width: 100%; padding-right: 0; text-align: center; order: 1;
        @media screen and (min-width:768px){
            padding-right: 15px; width: 50%; text-align: left; order: inherit;
        }
        @media screen and (min-width:991px){
            padding-right: 25px; 
        }
    }
    .right-part{
         width: 100%; text-align: center; order: 1;
        @media screen and (min-width:768px){
            padding-left: 15px; width: 50%; text-align: right; order: inherit;
        }
         @media screen and (min-width:991px){
            padding-left: 25px; 
        }
    }
    // common css 
    h2 {
        font-size: 25px; line-height: 33px; margin-top: 20px; text-align: left;
        @media screen and (min-width:991px){
            font-size: 43px; line-height: 50px;  margin-bottom: 15px;
        }
        @media screen and (min-width:1024px){
            font-size: 48px; line-height: 58px; margin-top: 0; margin-bottom: 15px;
        }
        a{
            text-decoration: none;
        }
    }
    p{
         font-size: 16px; line-height: 25px; margin-bottom: 10px; text-align: left; 
        @media screen and (min-width:568px){
             margin-bottom: 13px;
        }
        @media screen and (min-width:768px){
            margin-bottom: 15px; 
        }
        @media screen and (min-width:768px){
             font-size: 18px; line-height: 26px;
        }
    }
    .blog-card-cta{
        text-align: center;
        @media screen and (min-width:768px){
            text-align: left;
        }
        .line-btn{
            background-color: #fff; color: #000; border: 1px solid #000; transition: all 0.4s ease;  font-size: 15px; padding: 9px 10px;
            @media screen and (min-width:568px){
                font-size: 18px; padding: 13px;
            }
            @media screen and (min-width:991px){
                font-size: 21px; padding: 15px;
            }
            &:hover{
                background-color: #000; color: #fff;
            }
        }
    }
    a{
        display: inline-block;

        img{
            border-radius: 5px; width: auto;
        }
    }
    .mob-image-first{
        order: 0;
        @media screen and (min-width:768px){
            order: inherit;
        }
    }
}
.csd-category-product-grid-shortcode{
    padding: 21px 0px;
    @media screen and (max-width:768px){
        padding: 20px 0px;
    }
   
        .product:nth-child(n+5) {
            @media screen and (max-width:1024px){
                display: none;
        }
    }
}
.spacing-bottom{
    text-align: center;
    .button{
        background-color: #000;
        color: white;
        border-color: #000;
        padding: 12px 32px;
        cursor: pointer;
        transition: all .15s ease;
        border-width: 1px solid;
        font-size: 17px;
        line-height: 24px;
        margin-bottom: 0px;
        @media screen and (max-width:768px){
            padding: 8px 16px;
            font-size: 16px;
            line-height: 22px;
        }
        &:hover{
            color: #000;
            background-color: white;
            border-color: #000;
        }
    }
}
#trustpilot-widget-trustbox-0-wrapper{
    margin-bottom: 0 !important; display: none;
}
// Footer CSS Start 
.footer-trustpilot{
    display: none;
    margin-bottom: 15px;
    @media screen and (min-width:568px){
        margin-bottom: 20px;
    }
    .trustpilot-stars{
        font-weight: 700; text-decoration: none; font-size: 16px; line-height: 24px;
        @media screen and (min-width:568px){
            font-size: 18px; line-height: 26px;
        }
        .icon--trustpilot-stars{
            width: 100px;
        }
    }
}
.footer{
    padding: 0; border: 0px;
    @media screen and (min-width: 768px){
        padding: 20px 0 0 0;
    }
    .navUser-action--currencySelector{
        padding-left: 0;
    }
    .navUser-item--account{
        display: none;
    }
    .footer-currency{
        margin-left: 0; float: none; display: inline-block;
        .navUser-item-currency{
            display: block;
        }
        #currencySelection{
            display: none;
        }
    }
    .footer-top{
        text-align: center; padding-bottom: 30px;
        .footer-trustpilot{
            margin-bottom: 15px;
            @media screen and (min-width:568px){
                margin-bottom: 20px;
            }
            .trustpilot-stars{
                font-weight: 700; text-decoration: none; font-size: 16px; line-height: 24px;
                @media screen and (min-width:568px){
                    font-size: 18px; line-height: 26px;
                }
                .icon--trustpilot-stars{
                    width: 100px;
                }
            }
        }
        h3{
            margin-top: 0; display: inline-block; margin-bottom: 10px;color: #000000; position: relative; font-size: 18px; line-height: 24px; padding-bottom: 3px;

            &:after{
                content: ''; position: absolute; width: 30px; height: 2px; background-color: #000000; bottom: 0; left: 50%; transform: translateX(-50%);
            }
            @media screen and (min-width: 568px){
                font-size: 20px; line-height: 26px; padding-bottom: 5px;
            }
        }
        .footer-info-subtext{
            margin-bottom: 10px; font-size: 13px; line-height: 19px;
            @media screen and (min-width:568px){
                font-size: 14px; line-height: 23px;
            }
        }
        .form{
            margin-bottom: 0;
            .form-field{
                margin-bottom: 0;
                .form-prefixPostfix{
                    justify-content: center; position: relative; max-width: 420px; margin: 0 auto;
                    .form-input{
                         font-family: 'Montserrat', sans-serif;
                    }
                    .form-prefixPostfix-button--postfix{
                        position: absolute; right: 0; top: 0; margin-top: 0; width: auto; font-size: 21px; color: #000000; background-color: transparent;padding: 10px 19px;font-weight: 600;border: none;
                        &:hover{
                            color: #ffffff; background-color: #000000;
                        }
                    }
                }
            }
        }
    }
    .footer-mn{
        padding: 30px 0 10px 0;
        @media screen and (min-width: 768px){
            padding: 30px 0 15px 0;
        }
        @media screen and (min-width: 1199px){
            padding: 30px 0 20px 0;
        }
        h5, li, address, a, p{
            color: #ffffff;
        }
        background-color: #000000;
        .fmn-top{
            font-size: 0; 
            @media screen and (min-width:568px){
                margin-left: -5px; margin-right: -5px;
            }
            @media screen and (min-width:1024px){
                display: flex; flex-wrap: wrap;
            }
            .footer-info-col--small{
                width: 100%; padding-left: 0; padding-right: 0; text-align: left; position: relative; margin-bottom: 10px;
                @media screen and (min-width:568px){
                    width: 50%; padding-left: 5px; padding-right: 5px;
                }
                @media screen and (min-width:768px){
                    width: 33.333%;
                }
                @media screen and (min-width:1024px){
                    order: 1; margin-bottom: 30px;
                }
                @media screen and (min-width:1199px){
                    width: 16.66%;
                }
                h5{
                    font-size: 15px; line-height: 21px; font-weight: 600; text-transform: uppercase; margin-bottom: 7px;
                }
                .custom-dropdown{
                    position: absolute; top: 0; right: 0;
                    @media screen and (min-width:568px){
                        display: none;
                    }
                    svg{
                        fill: #ffffff; width: 90%; height: 90%; transition: all 0.3s ease-in-out;
                    }
                    &.open{
                        svg{
                            transform: rotate(180deg);
                        }
                    }
                }
                .footer-info-list{
                    margin-bottom: 10px ;
                    @media screen and (min-width:568px){
                        display: block !important;
                    }
                    @media screen and (min-width:1024px){
                        margin-bottom: 0;
                    }
                    li{
                        margin-bottom: 5px;
                        &:last-child{
                            margin-bottom: 0;
                        }
                        a{
                            position: relative; transition: all 0.4s ease-in-out;
                            &:hover{
                                opacity: 0.7;
                            }
                        }
                    }
                }
                .socialLinks{
                    margin-bottom: 10px;
                    .socialLinks-item {
                        margin-right: 5px;
                        .icon{
                            padding: 7px; border-radius: 50%; height: 32px; width: 32px; overflow: hidden; position: relative;
                            svg{
                                fill: #ffffff;
                            }
                            &.instagram {
                                background: #f09433;
                                background: linear-gradient(45deg,#f09433 0,#e6683c 25%,#dc2743 50%,#cc2366 75%,#bc1888 100%);
                            }
                            &.facebook {
                                background-color: #1877f2;
                            }
                            &.twitter, &.tumblr {
                                background-color: #1da1f2;
                            }
                            &.pinterest {
                                background-color: #bd081c;
                            }
                            &.linkedin {
                                background-color: #0a66c2;
                            }
                            &:after {
                                content: ''; position: absolute; width: 40px; background-color: rgba(255, 255, 255, 0.505); transform: rotate(54deg);
                                height: 10px; top: -2px; right: -21px; transition: all 0.4s ease;
                            }
                            &:hover:after{
                                top: -4px; right: -4px; width: 40px;height: 40px;
                            }
                        }
                        &:last-child{
                            margin-right: 0;
                        }
                    }
                }
                .sustainability-seal{
                    margin-bottom: 10px;
                    a{
                        height:70px;
                    }
                    img{
                        max-height: 70px; width: auto;
                    }
                }
                &.fmn-social{
                    text-align: center;
                    @media screen and (min-width:568px){
                        width: 100%;
                    }
                    @media screen and (min-width:1024px){
                        order: 2;
                    }
                }
                &.footer-store-details{
                    text-align: center; 
                    @media screen and (min-width:568px){
                        width: 100%;
                    }
                    @media screen and (min-width:1024px){
                        order: 0; width: 33.333%; text-align: left; 
                    }
                    @media screen and (min-width:1199px){
                        width: 16.4%; 
                    }
                    .footer-store-name{
                        text-transform: capitalize; font-size: 15px; line-height: 21px; font-weight: 500; letter-spacing: 1px;
                    }
                    address{
                        font-size: 14px;line-height: 20px;font-style: normal;font-family: 'Montserrat', sans-serif; margin-bottom: 15px;
                    }
                    .footer-info-phone{
                         font-weight: 600;
                        .icon{
                            display: inline-block; vertical-align: text-top;
                            svg{
                                fill: #ffffff;
                            }
                        }
                    }
                }
            }
        }
    }
    .fmn-copyright{
        border-top: 1px solid #ffffff57;padding-top: 10px;
        @media screen and (min-width: 768px){
            padding-top: 15px;
        }
        @media screen and (min-width: 1199px){
            padding-top: 20px;
        }
        p{
            font-size: 12px; line-height: 20px; margin-bottom: 0;
        }
        h5, li, address, a, p{
            color: #ffffff;
        }
    }
    .footer-btm{
        padding: 2px 0 2px 0; text-align: center;
        @media screen and (min-width:1199px){
            padding: 2px 0 2px 0;
        }
        .csd-payment-methods-wrapper {
            width: 100%;
            max-width: 800px;
            margin: 0 auto;
            .csd-payment-method{
                display: inline-block; position: relative; width: 56px;
                @media screen and (min-width:1199px){
                    width: 70px;
                }
                &.visa {
                    svg {
                        padding: 12%;
                    }
                }
                &::before{
                    padding-top: 60%;
                    content: '';
                    display: block;
                }
                >.icon {
                    width: 100%;
                    height: 100%;
                    position: absolute;
                    top: 0;
                    left: 0;
                    padding: 5%;
                }
            }
        }
    }
}
#consent-manager-update-banner {
    display: none!important; 
}
// Footer CSS End 
 .navPages-action-open{
    display: none;
}
.card .rrp-price--withoutTax{
    @media screen and (min-width: 768px){
        height: 20px;
    }
}
.page-product, .page-blog_post{
    #main-content{
        padding-bottom: 40px; 
    }
    .breadcrumbs{
        padding: 10px 0px;  margin-bottom: 0;
        @media screen and (min-width:568px){
                padding-top: 10px;
            }
    }
    .form-label--alternate{
        margin-bottom: 5px; font-size: 15px; color: #000000;
    }
}
.prdct-detail, .quickView{
    padding-top: 15px;
    @media screen and (min-width:568px){
        padding-top: 20px;
    }
    .productView-info{
        margin: 0;
    }
    .verified-and-authenticated{
        margin-bottom: 15px; margin-top: 15px;
        @media screen and (min-width:568px){
            margin-bottom: 20px;
        }
        @media screen and (min-width:801px){
            margin-bottom: 10px;
        }
    }
    .badge-sold-out-large {
        width: 100%; display: inline-block; background-color: #989898; color: #ffffff; letter-spacing: 1px; font-size: 17px; font-weight: 600; text-align: center; padding: 8px 20px; margin-bottom: 20px; border-radius: 4px;
        @media screen and (min-width: 475px) {
            width: 215px;
        }
    }
    .out_of_stock_message {
        .oos_message_title {
            font-size: 26px; font-weight: 700; line-height: 32px; margin-bottom: 10px; display: inline-block;
        }
        .oos_message_subtitle {
            font-size: 20px; font-weight: 500; line-height: 28px;
        }
    }
    .view-more-btn{
        width: 100%; display: inline-block; background: #000; border-color: #000; letter-spacing: 1px; font-size: 17px; font-weight: 600; text-align: center; padding: 10px 20px; margin-bottom: 20px; border-radius: 4px;

