// Updated thumbnail slider css START
.productView-thumbnail-link{
    border: none !important; margin-bottom: 5px; max-width: 92px;
    img{
        width: auto !important; 
    }
}
.slider_thumbnail{
    width: 100% !important; min-width: inherit !important;
    @media screen and (min-width:801px){
        width: 100px !important;
    }    
}
.slider_image {
    width: calc(100% - 100px) !important; min-width: inherit !important;
    .slick-prev {
        top: -30px; left: -68px; transform: rotate(90deg); width: 30px;
    }
    .slick-next{
        left: -69px;bottom: -20px; top: inherit;transform: rotate(90deg); width: 30px;
        @media screen and (min-width:801px){
            bottom: -38px;
        }
        @media screen and (min-width:1025px){
            bottom: -8%;
        }
        @media screen and (min-width:1280px){
            bottom: -3%;
        }
        @media screen and (min-width:1366px){
            bottom: 8%;
        }
        @media screen and (min-width:1400px){
            bottom: 12%;
        }
        @media screen and (min-width:1600px){
            bottom: 16%;
        }
    }
}
.slick-next::before, .slick-prev::before, .slick-next:hover::before, .slick-prev:hover::before {
   opacity: 1;  width: 27px;
}
.slick-next::before, .slick-next:hover::before{
    background-image: url('data:image/svg+xml;base64,');
}
.slick-prev::before, .slick-prev:hover::before{
    background-image: url('data:image/svg+xml;base64,');
}
.small-slider .slick-dots{
    background-color: #ffffff; bottom: -20px;
    @media screen and (max-width:800px){
        display: flex !important; position: absolute;
        li {
            height: 15px; width: 100%; padding: 0; margin-bottom: 20px;
            button{
                height: 2px; width: 100%; border-color: #ccc; border-radius: 0;
                &:before{
                    height: 2px; width: 0%;left: 0; transition: all 0.4s ease-in-out; border-radius: 0;
                }
            }
            &.slick-active{
                button{
                    &:before{
                        width: 100%;
                    }
                }
            }
        }
    }
}          
.productView-thumbnail-link{
    @media screen and (max-width:800px){
       height: 100%; max-width: 100%;
    }
