//description tabs 
.description-tabs{
    .tabs{
        border: 0;
        padding-top: 13px;
    }
    .tab{
        float: none;
        border: none;
        &.is-active{
            border: none;
        }
    }
    .tabcontent{
        padding: 0 0 10px;
    } 
    .tabtitle{
        font-size: 16px;
        text-transform: uppercase;
        padding: 0 0 10px 16px;
        margin-bottom: 0px;
        color: #000;
        position: relative;
        text-align: left;
        &:before{
            display: inline-block;
              position: absolute;
              width: 8px;
              height: 8px;
              background: transparent;
              content: '';
              border-top: 2px solid #000;
              border-left: 2px solid #000;
              transition: all 250ms ease-in-out;
              text-decoration: none;
              color: transparent;
              transform: rotate(-135deg);
              left: 0;
              top: 4px;
        }
        &.is-active{
            &:before{
                    top: 8px;
                    transform: rotate(45deg);
                }
        }
    }
}


