style.scss:
*{
    a{
        text-decoration: none;
        &:hover{
            color: #000;
        }
    }
} 
.custom-fields-list .custom-field:last-child {
    .separator {
        display: none;
    }
}
.csd-payment-method.paypal {
    margin-right: 0;
}
.csd-payment-method.apple-pay, .csd-payment-method.gpay{
    svg{ padding: 10% 0; }
}
.apple-pay, .klarna, .clearpay{
    svg{ fill:#000; }
}
.container-product {
    .page-heading {
        text-align: left;
        font-size: 20px;
        line-height: 28px;
        margin-bottom: 15px;
        @media screen and (min-width:568px){
            font-size: 24px;
            line-height: 32px;
            margin-bottom: 15px; 
        }
        @media screen and (min-width:991px){
            font-size: 30px;
            line-height: 35px;
            margin-bottom: 30px; 
        }
        a{
            text-decoration: none;
            color: #000;
            font-weight: 400;
            &:hover{ 
                color: #787878;
                transition: all 0.15s ease-in-out;
            }
        }
        
    }
     .category-description{
        display: none;
    }
    #CsdCategoryProductsSortOrder {
    display: none!important;
}
    
}
.productGrid{
    display: flex;
    flex-wrap: wrap;
    
}
.productGrid .product {
    width: 50%;
    padding: 5px 10px 0px;
    margin-bottom: 20px;
    @media screen and (min-width:480px){
        width: 33.33%;
        padding: 5px 5px 0px;
        margin-bottom: 15px;
    }
}

.page-category .productGrid .product {
    width: 50%;
    @media screen and (min-width:480px){
        width: 33.33%;
    }
}
.page-default .productGrid .product {
    width: 50%;
    @media screen and (min-width:576px){
        width: 33.33%;
    }
    @media screen and (min-width:1024px){
        width: 25%;
    }
}

// home page custom card start

.card{
    margin: 0px; 
    position: relative;
    height: 100%;
    display: flex;
    flex-wrap: wrap;
    .card-figure{
        position: relative; 
        width: 100%;
        .badge-wishlist-wrapper{
            position: relative;
            .card-img-container{
                max-width: 300px;
                max-height: 300px;
                width: 100%;
                .card-alt-image{                    
                    .card-image {
                        position: absolute;
                        top: 0;
                        bottom: 0;
                        left: 0;
                        right: 0;
                        margin: auto;
                        border: 0;
                        max-height: calc(100% - 2px);
                        -o-object-fit: contain;
                        object-fit: contain;
                        font-family: 'object-fit: contain;';
                    }
                }                
            }
            .product-wishlist {
                display: none; position: absolute; top: 0; right: 0; z-index: 2;
                &.wishlists-initialized {
                    display: block;
                    .icon {
                        width: auto; text-align: right;
                        svg{
                            width: auto;
                        }
                    }
                    .wishlist-solid {
                        display: none;
                    }
                    &.has-in-wishlists {
                        .wishlist-solid {
                            display: block;
                        }
                        .wishlist-outline {
                            display: none;
                        }
                    }
                }
            }
        }
        
        .card-figcaption{
            
            .card-figcaption-body {
                display: none;
                @media screen and (min-width:991px){
                    display: flex;
                    justify-content: center;
                    pointer-events: all;
                    top: 75%;
                    transform: translate3d(0, -75%, 0);
                    color: #4e4e4e;
                    transform: none;
                    width: 100%;
                    text-align: left;
                }
                @media screen and (min-width:1199.98px){
                    display: flex;
                    justify-content: center;
                    pointer-events: all;
                    top: 80%;
                    transform: translate3d(0, -80%, 0);
                    color: #4e4e4e;
                    transform: none;
                    width: 100%;
                    text-align: left;
                }
                
                .card-figcaption--action-buttons {
                    display: flex;
                    flex-flow: row nowrap;
                    align-items: center;
                    justify-content: space-between;
                    align-items: center;
                }
            
            .card-figcaption--action-buttons{
                display: flex;
                flex-direction: row;
                justify-content: center;
                align-items: center;
                @media screen and (min-width:991px){
                    flex-direction: column;
                }
                @media screen and (min-width:1199.98px){
                    flex-direction: row;
                }
                .card-figcaption-button {
                    border: none;
                    display: block;
                    color: #000000;
                    background-color: #ffffff;
                    border: 1px solid #000000;
                    font-size: 15px;
                    font-weight: 600;
                    padding: 7px 5px;
                    letter-spacing: 0px;
                    border-radius: 4px;
                    margin: auto;
                        &:hover{
                            color: #ffffff;
                            background-color: #000000;
                            transition: all 0.15s ease-in-out;
                        }
                        @media screen and (min-width:991px){
                            font-size: 14px;
                            padding: 5px 15px;
                        }
                        @media screen and (min-width:1199.98px){
                            padding: 7px 9px;
                            letter-spacing: 1px;
                            font-size: 14px;
                        }
                }
                .quickview{
                    padding: 7px 5px;
                    border: none;
                    display: flex;
                    color: #000000;
                    background-color: #ffffff;
                    border: 1px solid #000000;
                    font-size: 15px;
                    font-weight: 600;
                    letter-spacing: 0px;
                    border-radius: 4px;
                    align-items: center;
                    justify-content: center;
                    margin: auto 5px auto auto;
                        @media screen and (min-width:991px){
                            font-size: 14px;
                            padding: 5px 15px;
                            margin: 0px 0px 10px 0px;
                        }
                        @media screen and (min-width:1199.98px){
                            padding: 7px 9px;
                            letter-spacing: 1px;
                            margin: auto 5px auto auto;
                            font-size: 15px;
                        }
                    }
                .button-card-add-to-cart{
                    padding: 7px 5px;
                    display: flex;
                    text-align: center;
                    align-items: center;
                    color: #000000;
                    background-color: #ffffff;
                    border: 1px solid #000000;
                    margin: auto;
                    font-size: 15px;
                        &:hover{
                            color: #ffffff;
                            background-color: #000000;
                            transition: all 0.15s ease-in-out;
                            font-weight: 600;
                        }
                        @media screen and (min-width:991px){
                            font-size: 15px;
                            padding: 5px 15px;
                        }
                        @media screen and (min-width:1199.98px){
                            padding: 7px 9px;
                            letter-spacing: 1px;
                            font-size: 15px;
                        }
                    }
                
            }
        }
    }
    .badges{
        position: absolute;
        content: " ";
        top: 83%;
        right: 0;
            @media screen and (min-width:768px){
                top: 86%;
            }
            .icon {
                width: 64px;
                @media screen and (min-width:768px){
                    width: 120px;
                }
            }
        }
    }
    .card-body{
        text-align: center; width: 100%; 
            .custom-wishlist {
                display: inline;
                color: transparent;
                background-color: transparent;
                position: absolute;
                top: 0;
                right: 0;
                padding: 0px;
                @media screen and (min-width:768px){
                    padding: 0px 20px 0px 0px;
                }
             }
            .brand-name{
                font-size: 14px;
                line-height: 14px;
                color: #000000;
                font-weight: 600;
                min-height: 28px;
            }

            .card-title {
                font-family: Montserrat,Arial,Helvetica,sans-serif!important;
                flex: none;
                overflow: hidden;
                line-height: 1.1;
                height: 31px;
                margin: 6px 0;
                text-overflow: ellipsis;
                width: 100%;
                padding: 0px;
                font-size: 14px;
                letter-spacing: 0.25px;
                align-items: center;
                text-align: center;
                align-self: center;
                font-weight: 400 !important;
            }
            .card-fields{
                font-size: 14px;
                text-align: center;
                color: #000000;
                line-height: 1.1;
                .custom-fields-list {
                    font-size: 11px;
                    line-height: 1.1;
                    height: 18px;
                    white-space: nowrap;
                    overflow: hidden;
                    text-overflow: ellipsis;
                    font-weight: 400;
                }
            }
            .card-text{
                display: flex;
                justify-content: center;
                color:#000000;
                
            }

            .card-text .price-section {
                display: flex;
                flex-wrap: wrap;
                justify-content: center;  
            }
            .badge-sold {
                width: 50px;
                .icon {
                    width: 100%;
                }
            }
            .product-wishlist, .badge-sold {
                display: none; position: absolute; top: 0; right: 0; z-index: 2;
                &.wishlists-initialized {
                    display: block;
                    .icon {
                        width: auto; text-align: right;
                        svg{
                            width:auto;
                        }
                    }
                    .wishlist-solid {
                        display: none;
                    }
                    &.has-in-wishlists {
                        .wishlist-solid {
                            display: block;
                        }
                        .wishlist-outline {
                            display: none;
                        }
                    }
                }
            }
    }
}
.card-figcaption-listing{display: none;}
.page-category .card-figcaption-listing{display: inline-block; min-height: 44px;}
.page-brand .card-figcaption-listing{display: inline-block;}
.filterSearchpage .card-figcaption-listing{
    display: inline-block;
}
.page-category .card-figcaption{display: none;}
.page-brand .card-figcaption{display: none;}
.filterSearchpage .card-figcaption{
    display: none;
}

.card-figcaption-listing{
    width: 100%;
    text-align: center;
    margin-top: 5px;
    .card-figcaption-button {
        color: #ffffff;
        background-color: none;
        }
        a{
            background-color: #000000 !important;
            color: #ffffff !important; 
            font-weight: 500 !important;
            letter-spacing: 0.5px !important; 
            margin: 0px !important;
            font-size: 14px !important;
            padding: 10px 0px !important;
            border: 1px solid #000000 !important;
            @media screen and (min-width:575px){
                padding: 10px 0px ;
            }
            @media screen and (min-width:768px){
                border: 1px solid #4E87FF;
                padding: 13px 0px ;
            }
            &:hover{
                color: #ffffff !important;
                border: 1px solid #4E87FF !important;
                background-color: #000000 !important;
            }
        }        
    }



    .card-figcaption-listing{
        margin-top: 5px;
        opacity: 1;    
        margin-top: auto;  width: 100%;
        @media screen and (min-width:770.98px){
            opacity: 0;     
        }
    }
    .card:hover{
        .card-figcaption-listing{
            opacity: 1;
            transition: all ease-in-out 0.5s;
        }
    }


    .card-figure:hover .card-alt-img--hover .card-alt-image {
        opacity: 1;
        line-height: 1.1;
            @media screen and (max-width:991px){
                    opacity: 0;
                    line-height: unset;
            }
    }
    .card-figure:hover .card-alt-img--hover .card-main-image{
            @media screen and (max-width:991px){
                        opacity: 1 !important;
            }
    }

.card-alt-img--hover .card-alt-image, .card-alt-img--hover .card-main-image {
    transition: all .5s ease-in-out;
}

.card-alt-img--hover .card-alt-image {
    opacity: 0; 
}

.card-figure:hover .card-alt-img--hover .card-main-image {
    opacity: 0;
}

.card-alt-img--hover .card-main-image {
    opacity: 1;
}
.price-section .price {
    margin: 0;
    line-height: 1.1;
}
.card-text .price--listPrice {
    order: 2;
}

.card-text .price--saving{    line-height: 19px;    padding-right: 7px;}

.price--listPrice.has-sale-price {
    color: #a41315;
    line-height: 1.1;
}
.price--listPrice {
    display: flex;
    -ms-flex-wrap: nowrap;
    flex-wrap: nowrap;
    font-weight: 700;
}
.price-section .price--rrp {
    text-decoration: none;
    display: block;
    clear: both;
    flex: none;
    width: 100%;
}
.card-text .price--rrp {
    order: 3;
}
.price--discounted,.price--rrp {
    color: #191919;
    display: block;
    font-size: 14px;
    order: 1;
    line-height: 1.1;
    padding: 2px 0px;
}
.price--rrp::before {
    // content: "Est. Retail ";
    font-weight: 400;
}
.price .price--withoutTax{
    color: #000000 !important;
    font-weight: 600;
    line-height: 1.1;
    padding: 2px 0px;
}
.price--non-sale{
    color:#666;
    padding: 2px 0px;
    order: 1;    padding-right: 7px;
}


.card{
    .dropdown-menu.is-open {
        display: none;
    }
}


.productGridViewAll{
    text-align: center;
    margin-bottom: 0px;
    @media screen and (min-width:576px){
        margin-bottom: 0px;
    }
    .button.button--primary {
        padding: 7px 20px;
        font-size: 15px;
        line-height: 1;
        color: #000;
        background: #fff;
        margin: 0;
        @media screen and (min-width:991px){
            font-size: 21px;
            line-height: 1;
        }
        &:hover{
            color: #ffffff;
            background: #000000;
            transition: all 0.3s ease-in-out;
        }
    }
}
// home page custom card End 

// home page text banner start //
   #home-page-text-banner{
        @media screen and (max-width:991px){
            display: none;
        }
        .text-banner{
            h3{
                text-align: center;
                font-weight: 500;
                font-size: 24px;
                line-height: 30px;
            }
            p{
                font-size: 16px;
                font-weight: 400;
                a{
                    font-weight: 500;
                    color: #000000;
                    &:last-child{
                        font-weight: 700;
                    }
                }
            } 
        }
   }
.modal-close{
    margin-top: -1rem !important;
    margin-right: 0rem !important;
}
.modal-close:focus::after, .modal-close:focus::before{
    background: transparent;
    border: 0;              
}
// home page text banner End //

// Contect Us page Start //
.page-page_contact_form{
    .breadcrumbs{
        text-align: left;
        display: block;
        margin-top: 10px;
        .breadcrumb{
            margin-top: 10px;
        }
    } 
    
    .page {
        margin: 0; 
        .page-heading {
                margin-bottom:0px;
                text-align: left;
                color: #000000;
                font-size: 24px;
                font-weight: 500;
                font-family: Montserrat,Arial,Helvetica,sans-serif!important;
            @media screen and (min-width:991px){
                text-align: left;
                margin-bottom:21px;
                color: #000000;
                font-size: 24px;
                font-weight: 500;
                font-family: Montserrat,Arial,Helvetica,sans-serif!important;
            }
        }
        .page-content {
            width: 100%;
            padding: 0px 0px ;

            .form {
                margin: 0 0 50px;
                width: 100%;
                margin: 0 auto;
                @media screen and (min-width:991px){
                    width: 66.66%;
                }
                .form-row--half {
                        font-family: Montserrat,Arial,sans-serif!important;
                    .form-field {
                        .form-label {
                            color: #4e4e4e;
                            cursor: pointer;
                            display: block;
                            font-size: 14px;
                            line-height: 1.5;
                            margin-bottom: 7px;
                            font-weight: 600;
                            & small {
                                color: #989898;
                                font-weight: 600;
                            }
                        }
                    }

                    .form-input {
                        appearance: none;
                        background-color: #fff;
                        border-color: #000;
                        border-style: solid;
                        border-width: 1px;
                        border-radius: 0;
                        color: #4e4e4e;
                        display: block;
                        font-family: inherit;
                        font-size: 1rem;
                        height: 3rem;
                        margin: 0;
                        padding: .75rem 1rem;
                        transition: border-color .1s ease-out;
                        width: 100%;
                        border-radius: 4px;
                    }
                }
            }

            .form-field {
                font-family: Montserrat,Arial,sans-serif!important;
                .form-label {
                        color: #4e4e4e;
                        cursor: pointer;
                        display: block;
                        font-size: 14px;
                        line-height: 1.5;
                        margin-bottom: 7px;
                        font-weight: 600;
                    & small {
                        color: #989898;
                        font-weight: 600;
                    }
                }

                .form-input {
                    appearance: none;
                    background-color: #fff;
                    border-color: #000;
                    border-style: solid;
                    border-width: 1px;
                    border-radius: 0;
                    color: #4e4e4e;
                    display: block;
                    font-family: inherit;
                    font-size: 1rem;
                    height: 100px;
                    margin: 0;
                    padding: 7px 14px;
                    transition: border-color .1s ease-out;
                    width: 100%;
                    border-radius: 4px; 
                    resize: vertical;
                }
                
                .g-recaptcha {
                    transform:scale(0.925);
                    transform-origin:0 0;
                    margin-left: 10px;
                    }
            }

            .form-actions {
                .button {
                    background-color: transparent;
                    border: 1px solid #000000;
                    letter-spacing: 1px;
                    color: #000000;
                    font-size: 15px;
                    padding: 9px 10px;
                    font-weight: 600;
                    margin-bottom: 20px;
                    @media screen and (min-width:991px){
                        font-size: 21px;
                        line-height: 23px;
                        margin-bottom: 30px;
                        padding: 8px 10px;
                    }
                    &:hover {
                        background-color: #000000;
                        border: 1px solid #ffffff;
                        color: #ffffff;
                        transition: all 0.15s ease;
                    }
                }
            }
        }
    }
    .g-recaptcha {
        transform:scale(0.925);
        transform-origin:0 0;
        }
}
// Contect Us page End //

//Information Page start
.page-page{
    .account-navigation{
        margin-top: 20px;
        .navBar-item{
            text-align: left;
            margin-left: 0;
            margin-bottom: 10px;
            width: 100%;
            a{
                font-size: 14px;
                font-weight: 400;
                line-height: 21px;
                color: #989898;
            }
        }
    }
}
// Information Page END 

.page-page, .page-Consignor, .Terms{
    .page-heading{
        display: none;
    }
    h1{
        font-size: 18px;
        line-height: 21px;
        font-weight: 700;
        color: #191919;
        margin-bottom: 21px;
    }
}

.page-page{
    .page-content{
        padding: 0px;

        .privacy-policy-table{
            overflow-x: auto;
        }
        .table-data{
            width: 100% !important;
            margin: 0px !important;
            font-size: 14px;
            @media screen and (min-width:575px){
                font-size: 16px;
            }
            tbody{
                border: 1px solid #191919;
                padding: 0px;
                @media screen and (min-width:575px){
                    padding: 10px;
                }
                tr{
                    border: 1px solid #191919;
                    padding: 0px;
                    @media screen and (min-width:575px){
                        padding: 10px;
                    }
                    th{
                        border: 1px solid #191919;
                        padding: 0px;
                        font-weight: 700;
                        @media screen and (min-width:575px){
                            padding: 10px;
                        }
                    }
                    td{
                        border: 1px solid #191919;
                        padding: 0px;
                        @media screen and (min-width:575px){
                            padding: 10px;
                        }
                    }
                }
            }
        }
    }
    &.page-Authentication{
        p{
            span{
                color: #191919;
            }
        }
        .logo-text{
            font-size: 18px;
            font-weight: 400;
            line-height:27px;
            margin-bottom: 0;
        }
    }
}

// 404 Page start //
.page-404{
    .breadcrumbs{
        margin: 21px 0px;
    }
    .page{
        .page-content--centered{
            width: 100%;
            @media screen and (min-width:991px){
                margin: 0px auto ;
                width: 66.66%;
                float: none;
            }
        }
    }
    .page-content{

        .page-heading{
                font-family: Montserrat,Arial,Helvetica,sans-serif!important;
                font-weight: 500;
                font-size: 24px;
                letter-spacing: 1px;
                margin-bottom: 21px;
                color: #000000;
                display: block;
        }
        .u-textAlignCenter{
            font-size: 14px;
            font-weight: 400;
            font-family:Montserrat,Arial,Helvetica,sans-serif!important;
            color: #191919;
        }
        .form-prefixPostfix{
            .form-input{
                border: 1.5px solid #000000;
            }
            .form-prefixPostfix-button--postfix{
                background-color: transparent;
                border: 1.5px solid #000000;
                color: #000000;
                font-weight: 600;
                font-size: 15px;
                letter-spacing: 0.5px;
                &:hover{
                    background-color:#000000;
                    color: #ffffff;
                    transition: all 0.2s ease-in-out;
                }
                @media screen and (min-width:768px){
                    font-size: 21px;
                }
            }
        }
    }
}
// 404 Page End //

// login page start //
.page-login{
    .breadcrumbs{
        margin: 21px 0px;
    }
    .login{
        max-width: 100%;
        margin: 0px;
        .page-heading{
            font-size: 24px;
            font-weight: 500;
            font-family:Montserrat,Arial,Helvetica,sans-serif!important;
            color: #000000;
            margin-bottom: 21px;
            text-align: left;
        }
        .login-row{
            margin: 0px auto 0px auto;
            width: 100%;
            @media screen and (min-width:991px){
                width: 80%;
            }
                @media screen and (min-width:1199.98px){
                    width: 66.6667%;
                }
            .login-form{
                padding: 0px 10.5px;
                margin: 0px;
                width: 100%;
                @media screen and (min-width:991px){
                    width: 50%;
                }
                .form-field--input{
                    .form-label{
                        font-size: 14px;
                        font-weight: 500;
                        color: #191919;
                        font-family:Montserrat,Arial,Helvetica,sans-serif!important;    
                    }
                    .form-input{
                        background-color: transparent;
                        color: #191919;
                        border: 1.5px solid #191919;
                    }
                }
                .form-actions{
                    display: flex;
                    flex-wrap: wrap;
                    flex-direction: column;
                    text-align: left !important;
                        .forgot-password{
                            margin-bottom: 21px;
                            color: #191919;
                            font-weight: 500;
                        }
                        .button--primary{
                            margin-left: 0px;
                            background-color: #000000;
                            color: #ffffff;
                            border: 1.5px solid #000000;
                            font-size:15px;
                            letter-spacing: 1px;
                            font-weight: 500;
