// Custom wishlist page - start
.swym-empty-wishlist {
    text-align: center; 
    @media screen and (min-width:991px) {
        margin-top: 80px; 
    }
    p {
        color: #000000; font-size: 15px; font-weight: 500;
    }
    a {
        background: #000000; color: #ffffff; font-weight: 600; letter-spacing: 1px; font-size: 21px; border: 1px solid #000000; transition: all 0.3s ease-in-out;
        &:hover {
            color: #000000; background: #ffffff;
        }
    }
}
.swym-wishlist-page { 
    .productGrid {
        margin: 15px -20px 0px;
    }
    .swym-wishlist-product {
        width: 50%; position: relative;
        @media screen and (min-width: 479px) {
            width: 33%;
        }
        @media screen and (min-width: 1024px) {
            width: 25%;
        }
        .card {
            &.product-sold {
                opacity: 0.7;
            }
            .product-wishlist, .badge-sold {
                display: none; position: absolute; top: 0; right: 0; z-index: 2;
                &.wishlists-initialized {
                    display: block;
                    .icon {
                        width: auto; text-align: right;
                        svg{
                            width:auto;
                        }
                    }
                    .wishlist-solid {
                        display: none;
                    } 
                    &.has-in-wishlists {
                        .wishlist-solid {
                            display: block;
                        }
                        .wishlist-outline {
                            display: none;
                        }
                    }
                }
            }
            .badge-sold {
                width: 50px;
                .icon {
                    width: 100%;
                }
            }
            .card-img-container {
                max-height: 200px;
            }
            .card-link {
                .card-pricing {
                    display: flex; flex-wrap: wrap; align-items: center; min-height: 40px; 
                    span.price.price--withoutTax {
                        line-height: 1.3; color: #000000;
                    }
                    .price--rrp {
                        width: 100%; text-decoration: none;
                    }
                }
            }
            .card-details {
                align-self: flex-start;
                .form-action .button {
                    font-size: 14px; font-weight: 600; padding: 10px 5px; transition: all 0.2s ease-in-out;
                    @media screen and (min-width: 479px) {
                        font-size: 15px; padding: 10px;
                    }
                    &:hover {
                        border-color: #757575; background: #757575; color: #ffffff;
                    }
                }
                .swym-view-choose-options-btn, .button.swym-view-cart-btn, .swym-product-add-to-cart, .swym-sold-btn {
                    width: 100%; margin: 0;
                }
                .swym-product-add-to-cart, .swym-view-choose-options-btn {
                    background: #fff; color: #000; border: 1px solid #000000;
                }
                .button.swym-view-cart-btn {
                    background: #000000; color: #ffffff; border: 1px solid #000000;
                }
                .swym-sold-btn {
                    background: #757575; border-radius: 4px; display: block; font-weight: 600; padding: 10px; font-size: 15px; color: #000000; line-height: 14px;
                }
            }
        }
        &.in-cart .swym-product-add-to-cart, &:not(.in-cart) .swym-view-cart-btn {
            display: none;
        }
    }
}
// Custom wishlist page - end

